import React, { Component } from 'react';
import TodoItem from './TodoItem';
import TodoForm from './TodoForm';
import * as apiCalls from './api';

const APIURL = '/api/todos/';

class TodoList extends Component {
	constructor(props) {
		super(props);
		this.state = {
			todos: []
		};
		this.addTodo = this.addTodo.bind(this);
	}

	componentDidMount() {
		this.loadTodos();
	}

	async loadTodos() {
		const todos = await apiCalls.getTodos();
		this.setState({ todos });
	}

	async addTodo(val) {
		const newTodo = await apiCalls.createTodo(val);
		this.setState({ todos: [ ...this.state.todos, newTodo ] });
	}

	deleteTodo(id) {
		const deleteURL = APIURL + id;
		fetch(deleteURL, {
			method: 'delete'
		}).then((resp) => {
			if (!resp.ok) {
				if (resp.status >= 400 && resp.status < 500) {
					return resp.json().then((data) => {
						let err = { errMessage: data.message };
						throw err;
					});
				} else {
					let err = { errMessage: 'Please try again later, server is not responding' };
					throw err;
				}
			}
			return resp.json().then(() => {
				const todos = this.state.todos.filter((todo) => todo._id !== id);
				this.setState({ todos });
			});
		});
	}

	toggleTodo(todo) {
		const toggleURL = APIURL + todo._id;
		fetch(toggleURL, {
			method: 'put',
			headers: new Headers({
				'Content-Type': 'application/json'
			}),
			body: JSON.stringify({ completed: !todo.completed })
		}).then((resp) => {
			if (!resp.ok) {
				if (resp.status >= 400 && resp.status < 500) {
					return resp.json().then((data) => {
						let err = { errMessage: data.message };
						throw err;
					});
				} else {
					let err = { errMessage: 'Please try again later, server is not responding' };
					throw err;
				}
			}
			return resp.json().then((updatedTodo) => {
				const todos = this.state.todos.map(
					(t) => (t._id === updatedTodo._id ? { ...t, completed: !t.completed } : t)
				);
				this.setState({ todos });
			});
		});
	}

	render() {
		const todos = this.state.todos.map((t) => (
			<TodoItem
				key={t._id}
				{...t}
				onDelete={this.deleteTodo.bind(this, t._id)}
				onToggle={this.toggleTodo.bind(this, t)}
			/>
		));
		return (
			<div>
				<h1>Todo List</h1>
				<TodoForm addTodo={this.addTodo} />
				<ul>{todos}</ul>
			</div>
		);
	}
}

export default TodoList;
