import React, { Component } from 'react';
import TodoList from './TodoList';
import './App.css';

const APIURL = '/api/todos';

class App extends Component {
	constructor(props) {
		super(props);
		this.state = {
			todos: []
		};
	}

	componentDidMount() {
		this.loadTodos();
	}

	loadTodos() {
		fetch(APIURL).then((resp) => {
			if (!resp.ok) {
				if (resp.status >= 400 && resp.status < 500) {
					return resp.json().then((data) => {
						let err = { errMessage: data.message };
						throw err;
					});
				} else {
					let err = { errMessage: 'Please try again later, server is not responding' };
					throw err;
				}
			}
			return resp.json().then((todos) => this.setState({ todos }));
		});
	}
	render() {
		return (
			<div className="App">
				<TodoList />
			</div>
		);
	}
}

export default App;
