import React, { Component } from 'react';
import TodoList from './TodoList';
import './App.css';

const APIURL = '/api/todos';

class App extends Component {
	constructor(props) {
		super(props);
		this.state = {
			todos: []
		};
	}

	componentDidMount() {
		this.loadTodos();
	}

	loadTodos() {
		fetch(APIURL).then((data) => data.json()).then((todos) => this.setState({ todos }));
	}
	render() {
		return (
			<div className="App">
				<TodoList />
			</div>
		);
	}
}

export default App;
